mapper.update:
UPDATE blogDraft
SET draftTitle=#{draftTitle}, draftContent=#{draftContent}, draftTagName=#{draftTagName}
WHERE userId=#{userID};

mapper.select:
select count(*)
from blogDraft
where userId=#{userID};

select draftTitle, draftContent, draftTagName, userId
from blogDraft
where userId=#{userID};
